import React from "react";
import {
  Grid,
  makeStyles,
  TextField,
  Card,
  FormControl,
  InputLabel,
  Select,
  Typography,
} from "@material-ui/core";
import dayjs from "dayjs";
import { useQuill } from "react-quilljs";
import "quill/dist/quill.snow.css";

dayjs.locale("zh-hk");
const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const LocationInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;
  const { quill, quillRef } = useQuill();

  React.useEffect(() => {
    if (quill) {
      quill.clipboard.dangerouslyPasteHTML(states.description);
      console.log(quill.getText());
      quill.on("text-change", handleOnChange("description"));
    }
  }, [quill]);

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Building Name"
            size="small"
            fullWidth
            value={states.buildingName}
            onChange={handleOnChange("buildingName")}
          />
        </Grid>
        <Grid item xs={12}>
          <FormControl className={classes.formControl} fullWidth>
            <InputLabel htmlFor="district">District</InputLabel>
            <Select
              native
              value={states.district}
              onChange={handleOnChange("district")}
              inputProps={{
                name: "district",
                id: "district",
              }}
            >
              <option value="Hong Kong Island">Hong Kong Island</option>
              <option value="New Territories">New Territories</option>
              <option value="Kowloon">Kowloon</option>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Address"
            size="small"
            fullWidth
            value={states.address}
            onChange={handleOnChange("address")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Phone"
            size="small"
            type="number"
            value={states.phone}
            onChange={handleOnChange("phone")}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography variant="subtitle2" gutterBottom>
            Description
          </Typography>
          {/* <textarea
            label="Description"
            rows="6"
            cols="80"
            defaultValue={states.description}
            onChange={handleOnChange("description")}
          /> */}
        </Grid>
        <div style={{ width: 800, height: 300 }}>
          <div ref={quillRef} />
        </div>
      </Grid>
    </Card>
  );
};

export default LocationInfo;
