import React from "react";
import { CircularProgress, Grid, Typography, Divider } from "@material-ui/core";
import dayjs from "dayjs";
// fontawesome
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faExclamationTriangle } from "@fortawesome/free-solid-svg-icons";
// hook
import { useFetch } from "../../../hooks";
// component
import SignLanguagCard from "./SignLanguagCard";

const ClientSignLanguages = () => {
  const { data, isLoading } = useFetch("/signLanguages");

  const signLanguageList = data.map((information) => {
    if (getDiffDays(information.lastDate) <= 14) {
      return (
        <Grid key={information._id} item xs={12} sm={6} md={4}>
          <SignLanguagCard information={information} />
        </Grid>
      );
    }
    return null;
  });

  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h3" align="center" className={classes.title}>
            Deaf and Dumb People Location
          </Typography>
          <Divider />
        </Grid>
        <Grid container spacing={3} flexGrow="1">
          <Grid item xs={12}>
            {filterController}
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h5" align="center" className={classes.title}>
            {district}
          </Typography>
        </Grid>
        {isLoading ? (
          <Grid item xs={12}>
            <CircularProgress />
          </Grid>
        ) : (
          <Grid item xs={12}>
            <Grid container spacing={3}>
              {filterLocationList(district)}
            </Grid>
          </Grid>
        )}
      </Grid>
    </div>
  );
};

export default ClientSignLanguages;
