import React from "react";
import { Grid } from "@material-ui/core";

// components
import { DashboardContainer, LoadingButton } from "../../../../components";
import { CaseInfo } from "../components";
// hooks
import { usePost } from "../../../../hooks";
// config
import dataModel from "../config/data";

const AddSignLanguage = () => {
  const [states, setStates] = React.useState(dataModel);
  const [res, postMethod] = usePost({ url: "/signlanguages", payload: states });
  const [imgBase64, setImgBase64] = React.useState("");

  const handleOnChange = (name) => (e) => {
    setStates({ ...states, [name]: e.target.value });
  };

  const handleFileChange = (name) => (e) => {
    let file = e.target.files[0];
    console.log("hi");
    convertImgToBase64(file);
  };

  const convertImgToBase64 = (imageFile) => {
    if (imageFile) {
      let fileReader = new FileReader();
      fileReader.onload = function (fileLoadedEvent) {
        let srcData = fileLoadedEvent.target.result;
        setImgBase64(srcData);
        handleOnChange("imgURL");
      };
      fileReader.readAsDataURL(imageFile);
    }
  };

  return (
    <DashboardContainer title="Add a new sign language">
      <Grid container spacing={2}>
        <Grid item xs={12} md={12} lg={8}>
          <CaseInfo
            states={states}
            handleOnChange={handleOnChange}
            handleFileChange={handleFileChange}
          />
        </Grid>

        <Grid item xs={12}>
          <LoadingButton
            variant="contained"
            color="primary"
            onClick={postMethod}
            isLoading={res.isLoading}
          >
            SUBMIT
          </LoadingButton>
        </Grid>
      </Grid>
    </DashboardContainer>
  );
};

export default AddSignLanguage;
