import React from "react";
import {
  CircularProgress,
  Grid,
  Typography,
  makeStyles,
  Divider,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// components
import CultureList from "./CultureList";

const useStyles = makeStyles({
  title: {
    fontWeight: "bold",
  },
});

const ClientCulture = () => {
  const [posts, setPosts] = useState([]);
  const { data, isLoading } = useFetch("/cultures/group");
  const clasess = useStyles();
  const [currentPage, setCurrentPage] = React.useState(1);
  const [postsPerPage] = React.useState(10);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h3" align="center" className={clasess.title}>
              Deaf and Dumb People Culture
            </Typography>
            <Divider />
          </Grid>
          {setPosts(data)}
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                <CultureList data={data} />
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientCulture;
