import React from "react";
import {
  CircularProgress,
  Grid,
  FormControlLabel,
  Switch,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// components
import CultrueCards from "./CultureCards";

const ClientCulture = () => {
  const { data, isLoading } = useFetch("/cultures/group");

  const [state, setState] = React.useState({ searching: "" });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
    setState(({ searching }: [event.target.name]));
  };

  const titleCards = data.map((information) => (
    <FormControlLabel
      control={<Switch onChange={handleChange} name={information._id} />}
      label={information._id}
    />
  ));

  const cultureCards = data.map((information) => (
    <Grid key={information._id} item xs={12}>
      <CultrueCards
        countrySource={information._id}
        information={information.data}
      />
    </Grid>
  ));

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                {titleCards}
              </Grid>,
              <Grid item xs={12}>
                {cultureCards}
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientCulture;
