import React from "react";
import {
  CircularProgress,
  Grid,
  FormControlLabel,
  Switch,
  FormGroup,
} from "@material-ui/core";
// hook
import { useFetch } from "../../../hooks";
// components
import CultrueCards from "./CultureCards";

const ClientCulture = () => {
  const { data, isLoading } = useFetch("/cultures/group");
  const [checked, setChecked] = React.useState(false);
  const handleChange = () => {
    setChecked((prev) => !prev);
  };

  const titleCards = data.map((information) => (
    <FormGroup row>
      <FormControlLabel
        control={<Switch checked={checked} onChange={handleChange} />}
        label={information._id}
      />
    </FormGroup>
  ));

  const cultureCards = data.map((information) => (
    <Grid key={information._id} item xs={12}>
      <CultrueCards
        countrySource={information._id}
        information={information.data}
      />
    </Grid>
  ));

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                {titleCards}
              </Grid>,
              <Grid item xs={12}>
                {cultureCards}
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientCulture;
