import React from "react";
import {
  Grid,
  makeStyles,
  TextField,
  Card,
  Select,
  FormControl,
  InputLabel,
  Typography,
  Button,
} from "@material-ui/core";
import dayjs from "dayjs";
import imageToBase64 from "image-to-base64";

dayjs.locale("zh-hk");
const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const SignLanguageInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;
  const handleFileChange = (event) => {
    convertImgToBase64(event.target.files[0]);
  };

  const convertImgToBase64 = (imageFile) => {
    if (imageFile) {
      let fileReader = new FileReader();
      fileReader.onload = function (fileLoadedEvent) {
        let srcData = fileLoadedEvent.target.result;
        imageToBase64(srcData);
      };
      console.log(fileReader.readAsDataURL(imageFile));
    }
  };

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            size="small"
            defaultValue={states.name}
            onChange={handleOnChange("name")}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography variant="subtitle2" gutterBottom>
            Description
          </Typography>
          <textarea
            label="Description"
            rows="6"
            cols="80"
            defaultValue={states.description}
            onChange={handleOnChange("description")}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Gesture"
            size="small"
            fullWidth
            value={states.gesture}
            onChange={handleOnChange("gesture")}
          />
        </Grid>
        <Grid item xs={6}>
          <FormControl className={classes.formControl} fullWidth>
            <InputLabel htmlFor="district">District</InputLabel>
            <Select
              native
              value={states.district}
              onChange={handleOnChange("district")}
              inputProps={{
                name: "district",
                id: "district",
              }}
            >
              <option value="US">US</option>
              <option value="HongKong">Hong Kong</option>
            </Select>
          </FormControl>
        </Grid>
        <Button variant="contained" component="label">
          Upload Image
          <input type="file" hidden onChange={handleFileChange("imgURL")} />
        </Button>
      </Grid>
    </Card>
  );
};

export default SignLanguageInfo;
