import React from "react";
import {
  CircularProgress,
  Grid,
  FormControlLabel,
  Switch,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// components
import CultrueList from "./CultureList";
import CheckBox from "./CheckBox";

const ClientCulture = () => {
  const { data, isLoading } = useFetch("/cultures/group");

  const [state, setState] = React.useState(data.map((i) => (i = true)));

  const [searching, setSearch] = React.useState("");

  const titleCards = data.map((information) => (
    <FormControlLabel
      control={
        <Switch
          onChange={handleChange}
          checked={state.i}
          name={information._id}
        />
      }
      label={information._id}
    />
  ));

  const cultureList = data.map((information) => (
    <Grid key={information._id} item xs={12}>
      <CultrueList information={information.data} />
    </Grid>
  ));

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
    setSearch({ ...searching, searching: event.target.name });
    if (event.target.checked) {
    }
  };

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                {titleCards}
              </Grid>,
              <Grid item xs={12}>
                {cultureList}
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientCulture;
