import React from "react";
import {
  Grid,
  CardContent,
  Typography,
  Card,
  CardHeader,
  Divider,
  makeStyles,
  useTheme,
  CardActionArea,
  CardMedia,
  CardActions,
} from "@material-ui/core";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  CardContent: { backgroundColor: "#E5E5E5" },
});

const getHeaderColor = (countrySource) => {
  // eslint-disable-next-line react-hooks/rules-of-hooks
  const theme = useTheme();
  switch (countrySource) {
    case "Hong Kong":
      return theme.palette.primary.dark;
    case "US":
      return theme.palette.warning.dark;
    default:
      return theme.palette.success.dark;
  }
};

const CultureCards = (props) => {
  const classes = useStyles();
  const { countrySource, information } = props;

  const cultureList = information.map((data) => {
    return (
      <Grid item xs={12} md={12} key={data._id}>
        <Card className={classes.root}>
          <CardActionArea>
            <CardMedia
              component="img"
              alt="Contemplative Reptile"
              height="140"
              src=".../public/img/deafAndDumb.jpeg"
              title="Contemplative Reptile"
              href={this.props.recipe.href}
            />
            <CardContent>
              <Typography gutterBottom variant="h5" component="h2">
                Lizard
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                Lizards are a widespread group of squamate reptiles, with over
                6,000 species, ranging across all continents except Antarctica
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions>
            <Button size="small" color="primary">
              Share
            </Button>
            <Button size="small" color="primary">
              Learn More
            </Button>
          </CardActions>
        </Card>

        <Card className={classes.root}>
          <CardHeader
            titleTypographyProps={{ variant: "body1" }}
            style={{
              backgroundColor: getHeaderColor(countrySource),
              color: "white",
            }}
            title={data.name}
          />
          <CardContent className={classes.CardContent}>
            <Typography variant="body2">
              Description: {data.description}
            </Typography>
            <Divider />
            <Typography variant="body2">
              Report Dates: {data.reportDate}
            </Typography>
          </CardContent>
        </Card>
      </Grid>
    );
  });

  return (
    <Grid container spacing={3}>
      {cultureList}
    </Grid>
  );
};

export default CultureCards;
