import React from "react";
import {
  CircularProgress,
  Grid,
  FormControlLabel,
  Switch,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// components
import CultrueList from "./CultureList";

const CheckBox = () => {
  const { data, isLoading } = useFetch("/cultures/group");

  const [state, setState] = React.useState(data.map((i) => (i = true)));
  const [nameList, setNameList] = React.useState([]);
  const [count, setCount] = React.useState(0);
  const [filterLists, setFilterLists] = React.useState([]);
  const [body, setBody] = React.useState([]);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
    const newNameList = nameList;
    if (event.target.checked === true) {
      newNameList.push(event.target.name);
      setCount(count + 1);
    } else {
      newNameList.splice(event.target.name, 1);
      setCount(count - 1);
    }
    setNameList(newNameList);
    const filterList = data
      .filter((information) => information._id.includes(nameList))
      .map((a) => {
        console.log(a);
        return a;
      });
    setFilterLists(filterList);
    setBody(
      filterLists.map((information) => {
        return (
          <Grid key={information._id} item xs={12}>
            <CultrueList information={information.data} />
          </Grid>
        );
      })
    );
  };

  const CheckBox = data.map((information) => (
    <FormControlLabel
      control={
        <Switch
          onChange={handleChange}
          checked={state.i}
          name={information._id}
        />
      }
      label={information._id}
    />
  ));

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                {CheckBox}
              </Grid>,
              <Grid item xs={12}>
                {body}
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default CheckBox;
