import React from "react";
import {
  CircularProgress,
  Grid,
  Typography,
  Divider,
  makeStyles,
  Button,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// component
import SignLanguagCard from "./SignLanguagCard";

const useStyles = makeStyles((theme) => ({
  title: {
    fontWeight: "bold",
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const ClientSignLanguages = () => {
  const classes = useStyles();
  const { data, isLoading } = useFetch("/signLanguages/group");
  const [district, setDistrict] = React.useState("US");

  const handleChange = (event) => {
    setDistrict(event.target.value);
  };

  const filterController = (
    <div className={classes.root}>
      <Button variant="outlined" color="secondary">
        US
      </Button>
      <Button variant="outlined" color="primary">
        Hong Kong
      </Button>
    </div>
  );

  const filterSignLanguageList = (district) => {
    if (district === "US") {
      return data
        .filter((information) => information._id === "New Territories")
        .map((information) => (
          <Grid key={information._id} item xs={12} sm={12} md={12}>
            <SignLanguagCard information={information.data} />
          </Grid>
        ));
    } else if (district === "Hong Kong") {
      return data
        .filter((information) => information._id === "Hong Kong Island")
        .map((information) => (
          <Grid key={information._id} item xs={12} sm={12} md={12}>
            <SignLanguagCard information={information.data} />
          </Grid>
        ));
    }
  };

  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h3" align="center" className={classes.title}>
            Sign Language Dictionary
          </Typography>
          <Divider />
        </Grid>
        <Grid container spacing={3} flexGrow="1">
          <Grid item xs={12}>
            {filterController}
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h5" align="center" className={classes.title}>
            {district}
          </Typography>
        </Grid>
        {isLoading ? (
          <Grid item xs={12}>
            <CircularProgress />
          </Grid>
        ) : (
          <Grid item xs={12}>
            <Grid container spacing={3}>
              {filterSignLanguageList(district)}
            </Grid>
          </Grid>
        )}
      </Grid>
    </div>
  );
};

export default ClientSignLanguages;
