import React from "react";
import {
  CircularProgress,
  Grid,
  Typography,
  makeStyles,
  Divider,
  ButtonBase,
  Avatar,
} from "@material-ui/core";
// hook
import { useFetch } from "../../../hooks";
// components
import ListrningAidCard from "./ListrningAidCard";

const useStyles = makeStyles({
  title: {
    fontWeight: "bold",
  },
});

const ClientListeningAid = () => {
  const { data, isLoading } = useFetch("/listeningaids/group");
  const clasess = useStyles();

  const listeningAidList = data.map((information) => (
    <Grid key={information._id} item xs={12} sm={6} md={6} lg={4}>
      <ListrningAidCard information={information} />
    </Grid>
  ));

  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h3" align="center" className={clasess.title}>
            Listening Aid
          </Typography>
        </Grid>
        <Divider></Divider>
        <ButtonBase
          focusRipple
          key={image.title}
          className={classes.image}
          focusVisibleClassName={classes.focusVisible}
          style={{
            width: image.width,
          }}
        >
          <span
            className={classes.imageSrc}
            style={{
              backgroundImage: `url(${image.url})`,
            }}
          />
          <span className={classes.imageBackdrop} />
          <span className={classes.imageButton}>
            <Typography
              component="span"
              variant="subtitle1"
              color="inherit"
              className={classes.imageTitle}
            >
              {image.title}
              <span className={classes.imageMarked} />
            </Typography>
          </span>
        </ButtonBase>
        {isLoading ? (
          <CircularProgress />
        ) : (
          <Grid item xs={12}>
            {listeningAidList}
          </Grid>
        )}
      </Grid>
    </div>
  );
};

export default ClientListeningAid;
