import React from "react";
import { Grid, makeStyles, TextField, Card } from "@material-ui/core";
import dayjs from "dayjs";

dayjs.locale("zh-hk");
const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const ListeningAidInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            size="small"
            value={states.name}
            onChange={handleOnChange("name")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Brand"
            size="small"
            value={states.brand}
            onChange={handleOnChange("brand")}
          />
        </Grid>
        <Grid item xs={12}>
          <FormControl className={classes.formControl} fullWidth>
            <InputLabel htmlFor="district">District</InputLabel>
            <Select
              native
              value={states.district}
              onChange={handleOnChange("district")}
              inputProps={{
                name: "district",
                id: "district",
              }}
            >
              <option value="Hong Kong Island">Hong Kong Island</option>
              <option value="New Territories">New Territories</option>
              <option value="Kowloon">Kowloon</option>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Price"
            size="small"
            type="number"
            value={states.price}
            onChange={handleOnChange("price")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Mark"
            size="small"
            type="number"
            value={states.mark}
            onChange={handleOnChange("mark")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Description"
            size="small"
            fullWidth
            value={states.description}
            onChange={handleOnChange("description")}
          />
        </Grid>
      </Grid>
    </Card>
  );
};

export default ListeningAidInfo;
