import React from "react";
import { CircularProgress, Grid, makeStyles } from "@material-ui/core";
// hook
import { useFetch } from "../../../hooks";
// component
import LocationCard from "./LocationCard";

const useStyles = makeStyles({
  title: {
    fontWeight: "bold",
  },
});

const ClientLocation = () => {
  const clasess = useStyles();
  const { data, isLoading } = useFetch("/rules");

  const locationList = data.map((information) => (
    <Grid key={information._id} item xs={12} sm={6} md={4}>
      <LocationCard information={information} />
    </Grid>
  ));

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h3" align="center" className={clasess.title}>
              Deaf and Dumb People Culture
            </Typography>
            <Divider />
          </Grid>

          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                <CultureList data={data} />
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientLocation;
