import React from "react";
import {
  CircularProgress,
  Grid,
  FormControlLabel,
  Switch,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// components
import CultrueList from "./CultureList";

const ClientCulture = () => {
  const { data, isLoading } = useFetch("/cultures/group");

  const [state, setState] = React.useState(data.map((i) => (i = true)));
  const [nameList, setNameList] = React.useState([]);
  const [count, setCount] = React.useState(0);
  const [show, setShow] = React.useState(true);

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
    const newNameList = nameList;
    if (event.target.checked === true) {
      newNameList.push(event.target.name);
      setCount(count + 1);
      setShow(false);
    } else {
      for (let i = 0; i < newNameList.length; i++) {
        if (newNameList[i] === event.target.name) {
          newNameList.splice(i, 1);
        }
      }
      setCount(count - 1);
      console.log(count);
      if (count === 1) {
        setShow(true);
      }
    }
    setNameList(newNameList);
  };

  const CheckBox = data.map((information) => (
    <FormControlLabel
      control={
        <Switch
          onChange={handleChange}
          checked={state.i}
          name={information._id}
        />
      }
      label={information._id}
    />
  ));

  const filterLists = data.map((information) => {
    for (let i = 0; i < nameList.length; i++) {
      if (information._id === nameList[i]) {
        return information;
      }
    }
  });

  // const filterList = filterLists.map((a, i) => {
  //   return a;
  // });

  const cultureList = data.map((information) => {
    return (
      <Grid key={information._id} item xs={12}>
        <CultrueList information={information.data} />
      </Grid>
    );
  });

  const filterCultureList = filterLists.map((information, i) => {
    if (information === undefined) {
      return "";
    } else {
      return (
        <Grid key={information._id} item xs={12}>
          <CultrueList information={information.data} />
        </Grid>
      );
    }
  });

  return (
    <div>
      <Grid item xs={12}>
        <Grid container spacing={3}>
          {isLoading ? (
            <Grid item xs={12}>
              <CircularProgress />
            </Grid>
          ) : (
            [
              <Grid item xs={12}>
                {CheckBox}
              </Grid>,
              <Grid item xs={12}>
                {show ? cultureList : filterCultureList}
              </Grid>,
            ]
          )}
        </Grid>
      </Grid>
    </div>
  );
};

export default ClientCulture;
