import React from "react";
import dayjs from "dayjs";
import {
  Grid,
  makeStyles,
  CardHeader,
  Card,
  CardContent,
  Typography,
  Divider,
  Avatar,
} from "@material-ui/core";

dayjs.locale("zh-hk");

const useStyles = makeStyles({
  root: {
    width: "100%",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  mb3: {
    marginBottom: "10px",
  },
  avatar: {
    backgroundColor: "white",
    color: "black",
  },
});

function getStatusColor(district) {
  if (district === "New Territories") {
    return "green";
  } else if (district === "Hong Kong Island") {
    return "blue";
  } else if (district === "Kowloon") {
    return "red";
  } else {
    return "#B71C1C";
  }
}

const LocationCard = (props) => {
  const classes = useStyles();
  const { information, district } = props;

  return (
    <Card className={classes.root}>
      <CardHeader
        titleTypographyProps={{ variant: "h6" }}
        style={{
          backgroundColor: getStatusColor(district),
          color: "white",
        }}
        title={information.buildingName}
      />
      <Divider />
      <CardContent>
        <Grid container spacing={2} className={classes.mb3}>
          <Grid item xs={12}>
            <Divider />
            <Typography variant="caption">{information.description}</Typography>
          </Grid>
        </Grid>
      </CardContent>
    </Card>
  );
};

export default LocationCard;
