import React from "react";
import { Grid, makeStyles, TextField, Card } from "@material-ui/core";
import dayjs from "dayjs";

const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const CultureInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            size="small"
            fullWidth
            value={states.name}
            onChange={handleOnChange("name")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Report Date"
            size="small"
            fullWidth
            type="date"
            value={dayjs(states.reportDate).format("YYYY-MM-DD")}
            onChange={handleOnChange("reportDate")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Country Source"
            size="small"
            fullWidth
            value={states.countrySource}
            onChange={handleOnChange("countrySource")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Description"
            size="small"
            fullWidth
            value={states.description}
            onChange={handleOnChange("description")}
          />
        </Grid>
      </Grid>
    </Card>
  );
};

export default CultureInfo;
