import React from "react";
import {
  Grid,
  makeStyles,
  TextField,
  Card,
  FormControl,
  InputLabel,
  Select,
} from "@material-ui/core";
import dayjs from "dayjs";

const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const CultureInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            size="small"
            fullWidth
            value={states.name}
            onChange={handleOnChange("name")}
          />
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Report Date"
            size="small"
            fullWidth
            type="date"
            value={dayjs(states.reportDate).format("YYYY-MM-DD")}
            onChange={handleOnChange("reportDate")}
          />
        </Grid>
        <Grid item xs={12}>
          <FormControl className={classes.formControl} fullWidth>
            <InputLabel htmlFor="type">Type</InputLabel>
            <Select
              native
              value={states.type}
              onChange={handleOnChange("type")}
              inputProps={{
                name: "type",
                id: "type",
              }}
            >
              <option value="BTE">BTE</option>
              <option value="ITE">ITE</option>
              <option value="RIC">RIC</option>
              <option value="RIC">ITC</option>
              <option value="RIC">CIC</option>
              <option value="RIC">IIC</option>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <TextField
            label="Description"
            size="small"
            fullWidth
            value={states.description}
            onChange={handleOnChange("description")}
          />
        </Grid>
      </Grid>
    </Card>
  );
};

export default CultureInfo;
