import React from "react";
import {
  Grid,
  makeStyles,
  TextField,
  Card,
  Select,
  FormControl,
  InputLabel,
  Typography,
} from "@material-ui/core";
import dayjs from "dayjs";

dayjs.locale("zh-hk");
const useStyles = makeStyles(() => ({
  root: {},
  content: {
    padding: "15px",
  },
}));

const SignLanguageInfo = (props) => {
  const classes = useStyles();
  const { states, handleOnChange } = props;
  const [imgBase64, setImgBase64] = React.useState("");

  const handleFileChange = (e) => {
    let file = e.target.files[0];
    convertImgToBase64(file);
  };

  const convertImgToBase64 = (imageFile) => {
    if (imageFile) {
      let fileReader = new FileReader();
      fileReader.onload = function (fileLoadedEvent) {
        let srcData = fileLoadedEvent.target.result;
        setImgBase64(srcData.toString());
      };
      fileReader.readAsDataURL(imageFile);
    }
  };

  return (
    <Card className={classes.content}>
      <Grid container spacing={2}>
        <Grid item xs={12}>
          <TextField
            label="Name"
            size="small"
            defaultValue={states.name}
            onChange={handleOnChange("name")}
          />
        </Grid>
        <Grid item xs={12}>
          <Typography variant="subtitle2" gutterBottom>
            Description
          </Typography>
          <textarea
            label="Description"
            rows="6"
            cols="80"
            defaultValue={states.description}
            onChange={handleOnChange("description")}
          />
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Gesture"
            size="small"
            fullWidth
            value={states.gesture}
            onChange={handleOnChange("gesture")}
          />
        </Grid>
        <Grid item xs={6}>
          <FormControl className={classes.formControl} fullWidth>
            <InputLabel htmlFor="district">District</InputLabel>
            <Select
              native
              value={states.district}
              onChange={handleOnChange("district")}
              inputProps={{
                name: "district",
                id: "district",
              }}
            >
              <option value="US">US</option>
              <option value="HongKong">Hong Kong</option>
            </Select>
          </FormControl>
        </Grid>
        <input type="file" name="imgURL" onChange={handleFileChange} />
        <img src={imgBase64} alt="img" width="200" height="200"></img>
      </Grid>
    </Card>
  );
};

export default SignLanguageInfo;
