import React from "react";
import {
  Grid,
  CardContent,
  Typography,
  Card,
  CardHeader,
  Divider,
  makeStyles,
  useTheme,
  CardActionArea,
  CardMedia,
  CardActions,
  Button,
} from "@material-ui/core";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  CardContent: { backgroundColor: "#E5E5E5" },
  CardActions: { backgroundColor: "#934A5F" },
});

const getHeaderColor = (countrySource) => {
  // eslint-disable-next-line react-hooks/rules-of-hooks
  const theme = useTheme();
  switch (countrySource) {
    case "Hong Kong":
      return theme.palette.primary.dark;
    case "US":
      return theme.palette.warning.dark;
    default:
      return theme.palette.success.dark;
  }
};

const CultureCards = (props) => {
  const classes = useStyles();
  const { countrySource, information } = props;

  const cultureList = information.map((data) => {
    return (
      <Grid item xs={12} md={12} key={data._id}>
        <Card className={classes.root}>
          <CardActionArea>
            <CardMedia
              component="img"
              alt={data.name}
              height="140"
              src="/build/img/deafAndDumb.jpeg"
              title={data.name}
            />
            <CardContent className={classes.CardContent}>
              <Typography gutterBottom variant="h5" component="h2">
                {data.name}
              </Typography>
              <Typography variant="body2" color="textSecondary" component="p">
                {data.description}
              </Typography>
            </CardContent>
          </CardActionArea>
          <CardActions className={classes.CardActions}>
            <Button size="small" color="primary">
              Share
            </Button>
            <Button size="small" color="primary">
              Learn More
            </Button>
          </CardActions>
        </Card>
      </Grid>
    );
  });

  return (
    <Grid container spacing={3}>
      {cultureList}
    </Grid>
  );
};

export default CultureCards;
