import React from "react";
import {
  CircularProgress,
  Grid,
  Typography,
  Divider,
  makeStyles,
} from "@material-ui/core";

// hook
import { useFetch } from "../../../hooks";
// component
import SignLanguagCard from "./SignLanguagCard";

const useStyles = makeStyles((theme) => ({
  title: {
    fontWeight: "bold",
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));
const ClientSignLanguages = () => {
  const { data, isLoading } = useFetch("/signLanguages");
  const classes = useStyles();
  return (
    <div>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Typography variant="h3" align="center" className={classes.title}>
            Deaf and Dumb People Location
          </Typography>
          <Divider />
        </Grid>
        <Grid container spacing={3} flexGrow="1">
          <Grid item xs={12}>
            {filterController}
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <Typography variant="h5" align="center" className={classes.title}>
            {district}
          </Typography>
        </Grid>
        {isLoading ? (
          <Grid item xs={12}>
            <CircularProgress />
          </Grid>
        ) : (
          <Grid item xs={12}>
            <Grid container spacing={3}>
              {filterLocationList(district)}
            </Grid>
          </Grid>
        )}
      </Grid>
    </div>
  );
};

export default ClientSignLanguages;
